pragma solidity ^0.5.5;

import "./ERC777.sol";
import "./libs/Ownable.sol";
import "./libs/Pausable.sol";

contract TokenBase is ERC777, Ownable, Pausable {
    event Minted(address indexed toAddress, uint256 indexed amount, string tx_id);
    event Burnt(uint256 indexed amount, string toAddress, address indexed fromAddress);

    // token information
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    uint256 private _totalSupply;

    constructor(
        string memory name,
        string memory symbol,
        address[] memory defaultOperators
    )
    ERC777(name, symbol, defaultOperators)
    public {}

    /* ===== Mint & Burn =====*/
    function mint(
        address account,
        uint256 amount,
        string memory tx_id
    ) public onlyOwner whenNotPaused returns (bool) {
        _mint(_msgSender(), account, amount, "", "");
        emit Minted(account, amount, tx_id);
        return true;
    }

    /*
        dex burn cToken from its banker address to withdraw token to user.
        Here useraddr is user conflux address, addr is user btc/eth address.
    */
    function burn(
        address useraddr,
        uint256 amount,
        string memory addr) public returns (bool) {
        _burn(_msgSender(), _msgSender(), amount, "", "");
        emit Burnt(amount, addr, useraddr);
        return true;
    }
}
